import React from 'react';
import { NavLink } from 'react-router-dom';
import userService from '../../infrastructure/userService';

const styles = {
    wrapper: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '60vh',
    },
    jumbotron: {
        background: '#fff',
        borderRadius: '16px',
        boxShadow: '0 0 24px 2px rgba(0,0,0,0.12)',
        padding: '3rem 2.5rem',
        maxWidth: '540px',
        width: '100%',
        margin: '0 auto',
        textAlign: 'center',
    },
    title: {
        fontSize: '2.2rem',
        fontWeight: 700,
        marginBottom: '1.2rem',
    },
    greeting: {
        fontSize: '1.5rem',
        fontWeight: 600,
        marginBottom: '1rem',
        color: '#007bff',
    },
    divider: {
        height: '2px',
        background: '#e0e0e0',
        margin: '1.2rem auto',
        width: '70%',
        borderRadius: '1px',
    },
    lead: {
        fontSize: '1.1rem',
        fontWeight: 500,
        marginBottom: '1.5rem',
    },
    link: {
        color: '#007bff',
        fontWeight: 600,
        textDecoration: 'underline',
    },
    actions: {
        marginTop: '1.5rem',
    },
    button: {
        margin: '0 0.75rem',
        padding: '0.75rem 2.5rem',
        fontSize: '1.1rem',
        borderRadius: '8px',
        border: 'none',
        background: '#007bff',
        color: '#fff',
        fontWeight: 600,
        cursor: 'pointer',
        textDecoration: 'none',
        display: 'inline-block',
    }
};

const StartPage = () => {
    const isAdmin = userService.isAdmin();
    const isRoot = userService.isRoot();
    const currentUserId = userService.getUserId();
    const username = userService.getUsername();

    const renderGuestView = () => (
        <div style={styles.wrapper}>
            <div style={styles.jumbotron}>
                <h2 style={styles.title}>Welcome to Alumni Networking Platform!</h2>
                <div style={styles.divider}></div>
                <p style={styles.lead}>
                    Please{' '}
                    <NavLink style={styles.link} exact="true" to="/login">
                        Login
                    </NavLink>{' '}
                    or{' '}
                    <NavLink style={styles.link} exact="true" to="/register">
                        Register
                    </NavLink>{' '}
                    if you don't have an account.
                </p>
                <div style={styles.divider}></div>
                <div style={styles.actions}>
                    <NavLink style={styles.button} to="/login" role="button">
                        Login
                    </NavLink>
                    <NavLink style={styles.button} to="/register" role="button">
                        Register
                    </NavLink>
                </div>
            </div>
        </div>
    );

    const renderUserView = () => (
        <div style={styles.wrapper}>
            <div style={styles.jumbotron}>
                <h3 style={styles.greeting}>Hello, {username}!</h3>
                <div style={styles.divider}></div>
                <h2 style={styles.title}>Welcome to Alumni Networking Platform!</h2>
                <div style={styles.divider}></div>
                <div style={styles.actions}>
                    <NavLink
                        style={styles.button}
                        to={`/home/comments/${currentUserId}`}
                        role="button"
                    >
                        Home
                    </NavLink>
                    <NavLink
                        style={styles.button}
                        to={`/home/profile/${currentUserId}`}
                        role="button"
                    >
                        Profile
                    </NavLink>
                    {(isAdmin || isRoot) && (
                        <NavLink
                            style={styles.button}
                            to={`/home/users/all/${currentUserId}`}
                            role="button"
                        >
                            All Users
                        </NavLink>
                    )}
                </div>
            </div>
        </div>
    );

    const isLoggedIn = !!localStorage.getItem('token');

    return (
        <div>
            {isLoggedIn ? renderUserView() : renderGuestView()}
        </div>
    );
};
